  function transformUtxos(utxos) {
      return utxos.map((utxo) => {
          return {
              txid: utxo.txId,
              vout: utxo.outputIndex,
              scriptPubKey: utxo.script.toHex('hex'),
              amount: utxo.satoshis / 1e8,
          }
      })
  };

  function getUtxoFromTransaction(txid, tx, vout) {
    return {
      txid: txid,
      vout: vout,
      scriptPubKey: tx.outputs[vout].script.toHex(),
      amount: tx.outputs[vout].satoshis / 1e8,
    }
  }

